IF OBJECT_ID('[DBO].[BASE64BD]') IS NOT NULL
    DROP PROCEDURE [DBO].BASE64BD
GO

CREATE PROCEDURE BASE64BD  
    @FOTO_USUARIO VARCHAR(MAX)
AS BEGIN 
	BEGIN TRY
		SET NOCOUNT ON;	
		
		SELECT cast(N'' as xml).value('xs:base64Binary(sql:variable("@FOTO_USUARIO"))', 'varbinary(max)')

		RETURN

	END TRY
	BEGIN CATCH
		--SE OPTIENEN POSIBLES ERRORES DEL SP
		DECLARE @ERRORMESSAGE NVARCHAR(4000), @ERRORSEVERITY INT, @ERRORSTATE INT
		SELECT	@ERRORMESSAGE = ERROR_MESSAGE(), @ERRORSEVERITY = ERROR_SEVERITY(), @ERRORSTATE = ERROR_STATE();
		SELECT 'R01' CODRESPUESTA, ERROR_MESSAGE() DESRESPUESTA
		RAISERROR (@ERRORMESSAGE, 1, @ERRORSTATE)
	END CATCH
END
GO

--SE PRUEBA SP
EXEC [DBO].[BASE64BD]
		@FOTO_USUARIO = '',
@CODIGO_USUARIO = 11


GO  
